(1, '|Quickfix by iSenseLabs', 'isenselabs_quickfix', 'iSenseLabs', '1.4', 'http://isenselabs.com', '<modification>\r\n    <name>|Quickfix by iSenseLabs</name>\r\n	<version>1.4</version>\r\n	<link>http://isenselabs.com</link>\r\n	<author>iSenseLabs</author>\r\n	<code>isenselabs_quickfix</code>\r\n\r\n	<file path=\"admin/controller/extension/installer.php\">\r\n		<operation error=\"skip\">\r\n			<search ><![CDATA[\'extension/installer/ftp\']]></search>\r\n			<add position=\"replace\"><![CDATA[\'extension/installer/localcopy\']]></add>\r\n		</operation>\r\n\r\n		<operation error=\"skip\">\r\n			<search ><![CDATA[\'url\'  => str_replace(\'&amp;\', \'&\', $this->url->link(\'extension/installer/\'. $this->rewrite_step(\'ftp\', \'localremove\'), \'token=\' . $this->session->data[\'token\'],]]></search>\r\n			<add position=\"replace\"><![CDATA[\'url\'  => str_replace(\'&amp;\', \'&\', $this->url->link(\'extension/installer/localcopy\', \'token=\' . $this->session->data[\'token\'],]]></add>\r\n		</operation>\r\n\r\n		<operation error=\"skip\">\r\n			<search ><![CDATA[\'url\'  => str_replace(\'&amp;\', \'&\', $this->url->link(\'extension/installer/\'. $this->rewrite_step(\'localcopy\', \'localremove\'), \'token=\' . $this->session->data[\'token\'],]]></search>\r\n			<add position=\"replace\"><![CDATA[\'url\'  => str_replace(\'&amp;\', \'&\', $this->url->link(\'extension/installer/localcopy\', \'token=\' . $this->session->data[\'token\'],]]></add>\r\n		</operation>\r\n\r\n		<operation>\r\n			<search><![CDATA[public function unzip() {]]></search>\r\n			<add position=\"before\"><![CDATA[			\r\n	public function localcopy() {\r\n		$this->load->language(\'extension/installer\');\r\n\r\n		$json = array();\r\n\r\n		if (!$this->user->hasPermission(\'modify\', \'extension/installer\')) {\r\n			$json[\'error\'] = $this->language->get(\'error_permission\');\r\n		}\r\n\r\n		if (VERSION == \'2.0.0.0\') {\r\n		    $directory = DIR_DOWNLOAD  . str_replace(array(\'../\', \'..\\\\\', \'..\'), \'\', $this->request->post[\'path\']) . \'/upload/\';\r\n		} else {\r\n		    $directory = DIR_UPLOAD  . str_replace(array(\'../\', \'..\\\\\', \'..\'), \'\', $this->request->post[\'path\']) . \'/upload/\';\r\n		}\r\n\r\n		if (!is_dir($directory)) {\r\n			$json[\'error\'] = $this->language->get(\'error_directory\');\r\n		}\r\n\r\n		if (!$json) {\r\n			// Get a list of files ready to upload\r\n			$files = array();\r\n\r\n			$path = array($directory . \'*\');\r\n\r\n			while (count($path) != 0) {\r\n				$next = array_shift($path);\r\n\r\n				foreach (glob($next) as $file) {\r\n					if (is_dir($file)) {\r\n						$path[] = $file . \'/*\';\r\n					}\r\n\r\n					$files[] = $file;\r\n				}\r\n			}\r\n\r\n			$root = dirname(DIR_APPLICATION).\'/\';\r\n\r\n			foreach ($files as $file) {\r\n				// Upload everything in the upload directory\r\n				$destination = substr($file, strlen($directory));\r\n\r\n				// Update from newer OpenCart versions:\r\n				if (substr($destination, 0, 5) == \'admin\') {\r\n					$destination = DIR_APPLICATION . substr($destination, 5);\r\n				} else if (substr($destination, 0, 7) == \'catalog\') {\r\n					$destination = DIR_CATALOG . substr($destination, 7);\r\n				} else if (substr($destination, 0, 5) == \'image\') {\r\n					$destination = DIR_IMAGE . substr($destination, 5);\r\n				} else if (substr($destination, 0, 6) == \'system\') {\r\n					$destination = DIR_SYSTEM . substr($destination, 6);\r\n				} else {\r\n					$destination = $root.$destination;\r\n				}\r\n\r\n				if (is_dir($file)) {\r\n					if (!file_exists($destination)) {\r\n						if (!mkdir($destination)) {\r\n							$json[\'error\'] = sprintf($this->language->get(\'error_ftp_directory\'), $destination);\r\n						}\r\n					}\r\n				}\r\n\r\n				if (is_file($file)) {\r\n					if (!copy($file, $destination)) {\r\n						$json[\'error\'] = sprintf($this->language->get(\'error_ftp_file\'), $file);\r\n					}\r\n				}\r\n			}\r\n		}\r\n\r\n		$this->response->addHeader(\'Content-Type: application/json\');\r\n		$this->response->setOutput(json_encode($json));\r\n	}]]></add>\r\n		</operation>\r\n	</file>	\r\n</modification>\r\n', 1, '2020-03-01 18:46:02'),
(2, 'Export/Import Tool (V3.22) for OpenCart 3.x', 'Export/Import Tool (V3.22) for OpenCart 3.x', 'mhccorp.com', '3.x-3.22', 'https://www.mhccorp.com', '<modification>\r\n	<name>Export/Import Tool (V3.22) for OpenCart 3.x</name>\r\n	<code>Export/Import Tool (V3.22) for OpenCart 3.x</code>\r\n	<version>3.x-3.22</version>\r\n	<author>mhccorp.com</author>\r\n	<link>https://www.mhccorp.com</link>\r\n	<file path=\"admin/controller/common/column_left.php\">\r\n		<operation>\r\n			<search><![CDATA[if ($this->user->hasPermission(\'access\', \'tool/upload\')) {]]></search>\r\n			<add position=\"before\"><![CDATA[\r\n			if ($this->user->hasPermission(\'access\', \'extension/export_import\')) {\r\n				$maintenance[] = array(\r\n					\'name\'	   => $this->language->get(\'text_export_import\'),\r\n					\'href\'     => $this->url->link(\'extension/export_import\', \'token=\' . $this->session->data[\'token\'], true),\r\n					\'children\' => array()		\r\n				);\r\n			}\r\n			]]></add>\r\n		</operation>\r\n	</file>\r\n	<file path=\"admin/language/*/common/column_left.php\">\r\n		<operation>\r\n			<search><![CDATA[$_[\'text_backup\']]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n$_[\'text_export_import\']             = \'Export / Import\';\r\n			]]></add>\r\n		</operation>\r\n	</file>\r\n</modification>\r\n', 0, '2020-03-01 18:46:18'),
(7, '1 simple url rewrite', '1 simple url rewrite', 'deeman', '1.0.0', 'http://simpleopencart.com', '\n            <modification>\n                <name>1 simple url rewrite</name>\n                <code>1 simple url rewrite</code>\n                <version>1.0.0</version>\n                <author>deeman</author>\n                <link>http://simpleopencart.com</link>\n\n                <file path=\"catalog/controller/startup/startup.php\">\n                    <operation error=\"skip\">\n                        <search><![CDATA[$this->registry->set(\'url\']]></search>\n                        <add position=\"after\"><![CDATA[$this->url->addRewrite(new Simple\\Rewrite($this->config));]]></add>\n                    </operation>\n                </file>\n            </modification>', 1, '2020-05-15 12:44:26'),
