INSERT INTO `oc_modification` (`modification_id`, `name`, `code`, `author`, `version`, `link`, `xml`, `status`, `date_added`) VALUES
(26, 'Local copy OCMOD by iSenseLabs', 'isensealabs_quickfix_ocmod', 'iSenseLabs', '1.3', 'http://isenselabs.com', '<modification>\r\n    <name>Local copy OCMOD by iSenseLabs</name>\r\n	<version>1.3</version>\r\n	<link>http://isenselabs.com</link>\r\n	<author>iSenseLabs</author>\r\n	<code>isensealabs_quickfix_ocmod</code>\r\n\r\n	<file path=\"admin/controller/extension/installer.php\">\r\n		<operation error=\"skip\">\r\n			<search ><![CDATA[\'url\'  => str_replace(\'&amp;\', \'&\', $this->url->link(\'extension/installer/ftp\', \'token=\' . $this->session->data[\'token\'],]]></search>\r\n			<add position=\"replace\"><![CDATA[\'url\'  => str_replace(\'&amp;\', \'&\', $this->url->link(\'extension/installer/localcopy\', \'token=\' . $this->session->data[\'token\'],]]></add>\r\n		</operation>\r\n\r\n		<operation>\r\n			<search><![CDATA[public function unzip() {]]></search>\r\n			<add position=\"before\"><![CDATA[			\r\n	public function localcopy() {\r\n		$this->load->language(\'extension/installer\');\r\n\r\n		$json = array();\r\n\r\n		if (!$this->user->hasPermission(\'modify\', \'extension/installer\')) {\r\n			$json[\'error\'] = $this->language->get(\'error_permission\');\r\n		}\r\n\r\n		if (VERSION == \'2.0.0.0\') {\r\n		    $directory = DIR_DOWNLOAD  . str_replace(array(\'../\', \'..\\\\\', \'..\'), \'\', $this->request->post[\'path\']) . \'/upload/\';\r\n		} else {\r\n		    $directory = DIR_UPLOAD  . str_replace(array(\'../\', \'..\\\\\', \'..\'), \'\', $this->request->post[\'path\']) . \'/upload/\';\r\n		}\r\n\r\n		if (!is_dir($directory)) {\r\n			$json[\'error\'] = $this->language->get(\'error_directory\');\r\n		}\r\n\r\n		if (!$json) {\r\n			// Get a list of files ready to upload\r\n			$files = array();\r\n\r\n			$path = array($directory . \'*\');\r\n\r\n			while (count($path) != 0) {\r\n				$next = array_shift($path);\r\n\r\n				foreach (glob($next) as $file) {\r\n					if (is_dir($file)) {\r\n						$path[] = $file . \'/*\';\r\n					}\r\n\r\n					$files[] = $file;\r\n				}\r\n			}\r\n\r\n			$root = dirname(DIR_APPLICATION).\'/\';\r\n\r\n			foreach ($files as $file) {\r\n				// Upload everything in the upload directory\r\n				$destination = substr($file, strlen($directory));\r\n\r\n				// Update from newer OpenCart versions:\r\n				if (substr($destination, 0, 5) == \'admin\') {\r\n					$destination = DIR_APPLICATION . substr($destination, 5);\r\n				} else if (substr($destination, 0, 7) == \'catalog\') {\r\n					$destination = DIR_CATALOG . substr($destination, 7);\r\n				} else if (substr($destination, 0, 5) == \'image\') {\r\n					$destination = DIR_IMAGE . substr($destination, 5);\r\n				} else if (substr($destination, 0, 6) == \'system\') {\r\n					$destination = DIR_SYSTEM . substr($destination, 6);\r\n				} else {\r\n					$destination = $root.$destination;\r\n				}\r\n\r\n				if (is_dir($file)) {\r\n					if (!file_exists($destination)) {\r\n						if (!mkdir($destination)) {\r\n							$json[\'error\'] = sprintf($this->language->get(\'error_ftp_directory\'), $destination);\r\n						}\r\n					}\r\n				}\r\n\r\n				if (is_file($file)) {\r\n					if (!copy($file, $destination)) {\r\n						$json[\'error\'] = sprintf($this->language->get(\'error_ftp_file\'), $file);\r\n					}\r\n				}\r\n			}\r\n		}\r\n\r\n		$this->response->addHeader(\'Content-Type: application/json\');\r\n		$this->response->setOutput(json_encode($json));\r\n	}]]></add>\r\n		</operation>\r\n	</file>	\r\n</modification>\r\n', 0, '2021-04-09 16:17:30'),
(29, 'Disable Admin API', 'Fix-API', 'Alex Sol', '1.3', 'http://ionline.su', '<?xml version=\"1.0\" encoding=\"utf-8\" ?>\n<modification>\n<name>Disable Admin API</name>\n<code>Fix-API</code>\n<version>1.3</version>\n<author>Alex Sol</author>\n<link>http://ionline.su</link>\n\n<file path=\"catalog/controller/api/login.php\">\n<operation error=\"skip\">\n<search>\n<![CDATA[if (!in_array($this->request->server[\'REMOTE_ADDR\'], $ip_data)) {]]>\n</search>\n<add position=\"before\">\n<![CDATA[\n\n$ip_data[] = $this->request->server[\'REMOTE_ADDR\'];\n\n]]>\n</add>\n</operation>\n\n<operation>\n<search>\n<![CDATA[\nif (!$json) {\n]]>\n</search>\n<add position=\"before\">\n<![CDATA[\n$json = false;\n]]>\n</add>\n</operation>\n\n<operation>\n<search>\n<![CDATA[\nif ($api_info) {\n]]>\n</search>\n<add position=\"before\">\n<![CDATA[\n$api_info = array(\'api_id\'=>mt_rand(1000,9999));\n]]>\n</add>\n</operation>\n\n<operation>\n<search>\n<![CDATA[\n$session = new Session($this->session->getId(), $sesion_name);\n]]>\n</search>\n<add position=\"before\">\n<![CDATA[\n$this->session->data[\'asen_api_id\'] = $api_info[\'api_id\'];\n]]>\n</add>\n</operation>\n\n</file>\n\n<file path=\"catalog/controller/api/{coupon,customer,currency,order,payment,reward,shipping,voucher}.php\">\n<operation error=\"skip\">\n<search>\n<![CDATA[\nif (!isset($this->session->data[\'api_id\'])) {\n]]>\n</search>\n<add position=\"replace\">\n<![CDATA[\n\n$asen_api_id = mt_rand(1000,9999);\nif (empty($asen_api_id)) {\n]]>\n</add>\n</operation>\n\n</file>\n<file path=\"catalog/controller/api/cart.php\">\n<operation error=\"skip\">\n<search>\n<![CDATA[\nif (!isset($this->session->data[\'api_id\'])) {\n]]>\n</search>\n<add position=\"replace\">\n<![CDATA[\nif (!isset($this->session->data[\'api_id\'])) {\n// $asen_api_id = mt_rand(1000,9999);\n// if (empty($asen_api_id)) {\n]]>\n</add>\n</operation>\n\n</file>\n</modification>', 0, '2022-02-07 17:14:42'),
(30, 'Отключение запроса на добавление IP при редактировании заказа', 'not_required_ip_for_api', 'mpn2005', '1.0.0', 'https://opencartforum.com/profile/26766-mpn2005/', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\r\n<modification>\r\n  <code>not_required_ip_for_api</code>\r\n  <name>Отключение запроса на добавление IP при редактировании заказа</name>\r\n  <version>1.0.0</version>\r\n  <author>mpn2005</author>\r\n  <link>https://opencartforum.com/profile/26766-mpn2005/</link>\r\n  \r\n  <file path=\"admin/model/sale/order.php\">\r\n    <operation>\r\n      <search><![CDATA[public function getOrder($order_id) {]]></search>\r\n      <add position=\"after\"><![CDATA[\r\n        $api_query = $this->db->query(\"SELECT * FROM `\" . DB_PREFIX . \"api_ip` WHERE api_id = \'\" . (int)$this->config->get(\'config_api_id\') . \"\' AND ip = \'\" . $this->db->escape($this->request->server[\'REMOTE_ADDR\']) . \"\'\");\r\n        if (!$api_query->rows) $this->db->query(\"INSERT INTO `\" . DB_PREFIX . \"api_ip` SET api_id = \'\" . (int)$this->config->get(\'config_api_id\') . \"\', ip = \'\" . $this->db->escape($this->request->server[\'REMOTE_ADDR\']) . \"\'\");\r\n      ]]></add>\r\n    </operation>\r\n  </file>\r\n      \r\n</modification>\r\n', 1, '2022-02-14 20:42:55'),
(31, 'Rent dates in order (edit)', 'rent_dates_edit_oc2', 'you', '1.0', '', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\r\n<modification>\r\n  <name>Rent dates in order (edit)</name>\r\n  <code>rent_dates_edit_oc2</code>\r\n  <version>1.0</version>\r\n  <author>you</author>\r\n\r\n  <!-- 1) ВИВІД ПОЛІВ У КАРТЦІ ЗАМОВЛЕННЯ -->\r\n  <file path=\"admin/view/template/sale/order_info.tpl\">\r\n    <!-- Після таблиці з користувацькими полями додамо форму -->\r\n    <operation>\r\n      <search><![CDATA[<div class=\"tab-pane\" id=\"tab-additional\">]]></search>\r\n      <add position=\"after\"><![CDATA[\r\n        <div class=\"panel panel-default\" style=\"margin-top:15px;\">\r\n          <div class=\"panel-heading\"><h3 class=\"panel-title\">Оренда (редагування)</h3></div>\r\n          <div class=\"panel-body\">\r\n            <form method=\"post\" action=\"<?php echo $this->url->link(\'sale/order/editRent\', \'token=\' . $this->session->data[\'token\'] . \'&order_id=\' . $order_id, true); ?>\" class=\"form-horizontal\">\r\n              <div class=\"form-group\">\r\n                <label class=\"col-sm-3 control-label\">Дата видачі оренди</label>\r\n                <div class=\"col-sm-9\">\r\n                  <input type=\"date\" name=\"rent_issue\" class=\"form-control\"\r\n                         value=\"<?php echo isset($order_custom[\'rent_issue\']) ? $order_custom[\'rent_issue\'] : \'\'; ?>\">\r\n                </div>\r\n              </div>\r\n              <div class=\"form-group\">\r\n                <label class=\"col-sm-3 control-label\">Дата повернення оренди</label>\r\n                <div class=\"col-sm-9\">\r\n                  <input type=\"date\" name=\"rent_return\" class=\"form-control\"\r\n                         value=\"<?php echo isset($order_custom[\'rent_return\']) ? $order_custom[\'rent_return\'] : \'\'; ?>\">\r\n                </div>\r\n              </div>\r\n              <div class=\"text-right\">\r\n                <button type=\"submit\" class=\"btn btn-primary\"><i class=\"fa fa-save\"></i> Зберегти</button>\r\n              </div>\r\n            </form>\r\n          </div>\r\n        </div>\r\n      ]]></add>\r\n    </operation>\r\n  </file>\r\n\r\n  <!-- 2) КОНТРОЛЕР: віддати в шаблон розпарсений custom_field та прийняти POST -->\r\n  <file path=\"admin/controller/sale/order.php\">\r\n    <!-- 2.1 Перед рендером order_info передати $order_custom -->\r\n    <operation>\r\n      <search><![CDATA[$data[\'header\'] = $this->load->controller(\'common/header\');]]></search>\r\n      <add position=\"before\"><![CDATA[\r\n        // Parse order custom_field to $data[\'order_custom\']\r\n        if (!empty($order_info[\'custom_field\'])) {\r\n          $decoded = json_decode($order_info[\'custom_field\'], true);\r\n          if (is_array($decoded)) {\r\n            $data[\'order_custom\'] = $decoded;\r\n          } else {\r\n            $data[\'order_custom\'] = array();\r\n          }\r\n        } else {\r\n          $data[\'order_custom\'] = array();\r\n        }\r\n      ]]></add>\r\n    </operation>\r\n\r\n    <!-- 2.2 Додати новий метод editRent -->\r\n    <operation>\r\n      <search><![CDATA[class ControllerSaleOrder extends Controller {]]></search>\r\n      <add position=\"after\"><![CDATA[\r\n        public function editRent() {\r\n          $this->load->language(\'sale/order\');\r\n          $this->load->model(\'sale/order\');\r\n\r\n          $order_id = (int)$this->request->get[\'order_id\'];\r\n\r\n          // Отримати поточний JSON\r\n          $query = $this->db->query(\"SELECT custom_field FROM `\" . DB_PREFIX . \"order` WHERE order_id = \'\" . (int)$order_id . \"\'\");\r\n          $custom = array();\r\n          if (!empty($query->row[\'custom_field\'])) {\r\n            $tmp = json_decode($query->row[\'custom_field\'], true);\r\n            if (is_array($tmp)) $custom = $tmp;\r\n          }\r\n\r\n          // Нові значення\r\n          $rent_issue  = isset($this->request->post[\'rent_issue\'])  ? $this->request->post[\'rent_issue\']  : \'\';\r\n          $rent_return = isset($this->request->post[\'rent_return\']) ? $this->request->post[\'rent_return\'] : \'\';\r\n\r\n          $custom[\'rent_issue\']  = $rent_issue;\r\n          $custom[\'rent_return\'] = $rent_return;\r\n\r\n          $this->db->query(\"UPDATE `\" . DB_PREFIX . \"order` SET custom_field = \'\" . $this->db->escape(json_encode($custom)) . \"\', date_modified = NOW() WHERE order_id = \'\" . (int)$order_id . \"\'\");\r\n\r\n          // Повідомлення та редірект назад у замовлення\r\n          $this->session->data[\'success\'] = \'Дати оренди збережено.\';\r\n          $this->response->redirect($this->url->link(\'sale/order/info\', \'token=\' . $this->session->data[\'token\'] . \'&order_id=\' . (int)$order_id, true));\r\n        }\r\n      ]]></add>\r\n    </operation>\r\n  </file>\r\n</modification>\r\n', 0, '2025-09-17 18:59:11'),
(32, 'WebModernix - Callbell Live Chat', 'webmodernix_callbelllivechat', 'Nick Papoutsis', '1.0', 'https://link.webmodernix.com/oc-marketplace-np', '<?xml version=\"1.0\" encoding=\"UTF-8\" ?>\r\n<modification>\r\n<name>WebModernix - Callbell Live Chat</name>\r\n<version>1.0</version>\r\n<code>webmodernix_callbelllivechat</code>\r\n<author>Nick Papoutsis</author>\r\n<link>https://link.webmodernix.com/oc-marketplace-np</link>\r\n\r\n\r\n    <file path=\"catalog/controller/common/footer.php\">\r\n        <operation>\r\n            <search><![CDATA[        public function index(          ]]></search>\r\n            <add position=\"after\"><![CDATA[\r\n\r\n                if (version_compare(VERSION, \'3\', \'>=\')) {\r\n                    $webmodernix_callbelllivechat_config_prefix  = \'module_webmodernix_callbelllivechat\';\r\n                } else if (version_compare(VERSION, \'2.3\', \'>=\')) {\r\n                    $webmodernix_callbelllivechat_config_prefix  = \'webmodernix_callbelllivechat\';\r\n                } else {\r\n                    $webmodernix_callbelllivechat_config_prefix  = \'webmodernix_callbelllivechat\';\r\n                }\r\n\r\n                $webmodernix_callbelllivechat_status = $this->config->get($webmodernix_callbelllivechat_config_prefix . \'_status\');\r\n                if ($webmodernix_callbelllivechat_status) {\r\n                    $data[\'webmodernix_callbelllivechat\'] = $this->config->get($webmodernix_callbelllivechat_config_prefix . \'_script\');\r\n\r\n                    $webmodernix_callbelllivechat_extra_javascript_code = $this->config->get($webmodernix_callbelllivechat_config_prefix . \'_extra_javascript_code\');\r\n\r\n                    if ( trim($webmodernix_callbelllivechat_extra_javascript_code) !== \'\' ) {\r\n                        $data[\'webmodernix_callbelllivechat\'] = $data[\'webmodernix_callbelllivechat\'] . \'<script>\' . $webmodernix_callbelllivechat_extra_javascript_code . \'</script>\';\r\n                    }\r\n\r\n                    $webmodernix_callbelllivechat_extra_css_code        = $this->config->get($webmodernix_callbelllivechat_config_prefix . \'_extra_css_code\');\r\n\r\n                    if ( trim($webmodernix_callbelllivechat_extra_css_code) !== \'\' ) {\r\n                        $data[\'webmodernix_callbelllivechat\'] = \'<style>\' . $webmodernix_callbelllivechat_extra_css_code . \'</style>\' . $data[\'webmodernix_callbelllivechat\'];\r\n                    }\r\n\r\n                    $data[\'webmodernix_callbelllivechat\'] = html_entity_decode($data[\'webmodernix_callbelllivechat\'], ENT_QUOTES, \'UTF-8\');\r\n\r\n                } else {\r\n                    $data[\'webmodernix_callbelllivechat\'] = \'<!-- webmodernix_callbelllivechat disabled -->\';\r\n                }\r\n\r\n            ]]></add>\r\n        </operation>\r\n\r\n    </file>\r\n\r\n\r\n\r\n    <file path=\"catalog/view/theme/*/template/common/footer.tpl\" v=\"2\">\r\n        <operation>\r\n            <search><![CDATA[         </body          ]]></search>\r\n            <add position=\"before\"><![CDATA[\r\n\r\n                <?php echo \'<!-- webmodernix_callbelllivechat -->\'; ?>\r\n                <?php if ( isset($webmodernix_callbelllivechat) && !empty($webmodernix_callbelllivechat) ) {\r\n                    echo $webmodernix_callbelllivechat;\r\n                } ?>\r\n\r\n            ]]></add>\r\n        </operation>\r\n\r\n    </file>\r\n\r\n\r\n    <file path=\"catalog/view/theme/*/template/common/footer.twig\" v=\"3\">\r\n        <operation>\r\n            <search><![CDATA[         </body          ]]></search>\r\n            <add position=\"before\"><![CDATA[\r\n                    <!-- webmodernix_callbelllivechat -->\r\n                    {{ webmodernix_callbelllivechat }}\r\n\r\n            ]]></add>\r\n        </operation>\r\n\r\n    </file>\r\n\r\n\r\n\r\n</modification>\r\n', 1, '2025-09-22 22:31:16');

